import React from 'react'
import styled from 'styled-components';

const Input = styled.input`
  width: 343px;
  height: 48px;
  padding-left: 50px;
  margin-bottom: 20px;
  border: none;
  letter-spacing: 2px;
  font-weight: 100;
  ::placeholder{
      color: lightgrey;
  }
`

function InputField(props) {
  render(
      <input type="text" value={props.value} onChange={props.handleChange} />
  )
}

export default InputField
