import React from 'react'
import styled from 'styled-components';
import {Bootstrap, Grid, Row, Col, Container} from 'react-bootstrap';
import {Task, TaskList} from './../components/molecules/Tasks.js';

window.id = 0;

const StyledContainer = styled(Container)`
  && {
    padding-left: 25px;
    padding-right: 25px;
    padding-top: 25px;
  }
`;

class Tasks extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: ''
    }

    //this.getTemperature = this.getTemperature.bind(this);
    //this.getDays = this.getDays.bind(this);

  }

  addTask(val){
      // Assemble data
      const task = {text: val, id: window.id++}

      //Update data

      this.state.data.push(task);

      //Update state

      this.setState({data: this.state.data});
  }

  handleRemove(id) {
    // Filter all tasks except the one to be removed

    const remainder = this.state.data.filter((task) => {
      if(task.id !== id) return task;
    });

    //Update state with filter
    this.setState({data: remainder});

  }

  render() {
    return (
      <StyledContainer>
          <p> Taken </p>
          <TodoForm>
          </TodoForm>

          <TodoList>
          </TodoList>

      </StyledContainer>
    )
  }
}

export default Tasks;
