import React from 'react'
import styled from 'styled-components'
import Phase1 from '../svg/Phase1';
import Phase2 from '../svg/Phase2';


class CompostTransition extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      temperature: '',
      day: '',
    }

    this.getTemperature = this.getTemperature.bind(this);
    this.getDays = this.getDays.bind(this);

  }

  componentDidMount() {
      this.getTemperature()
      this.getDays()
  }

  getTemperature() {
      let currentTemperature = 21;
      this.setState({temperature: currentTemperature});
  }

  getDays() {
      let currentDay = 5;
      this.setState({day: currentDay});
  }


  render() {
    return(
      <div>
          <p> {this.state.temperature} </p>
          <p> {this.state.day} </p>
      </div>
    )
  }
}

export default CompostTransition;
