import React from 'react'
import styled from 'styled-components'


class CompostTransition extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      transition: ''
    }
  }

  componentDidMount() {
    this.getTransition()
  }

  getTransition() {

    const temperature = 36;
    const days = 2;
    let status = '';

    if(temperature >= 20 && temperature < 40 && days >= 3 && days < 14) {
        status = 'Je zit in fase 1';
    } else if(temperature < 35 && days > 3) {
        status = 'Het gaat te snel';
    } else {
        status = 'Er gaat iets mis';
    }



    this.setState({transition: status})
  }

  render() {
    return(
      <div>
          <p> {this.state.transition} </p>
      </div>
    )
  }
}

export default CompostTransition;
