import styled from 'styled-components';
import React from 'react';
import InputField from '../atoms/InputField'
import Button from '../../components/atoms/Button'
import SvgEmail from '../../components/svg/Email'
import SvgPassword from '../../components/svg/Password'
import SvgLogo from '../../components/svg/Logo'


const Anchor = styled.a`
  color: #FFC759;
  font-size: 12px;
  letter-spacing: 1px;
  font-weight: 500;
  &:hover {
      text-decoration: none;
      color: #FFC759;
  }
`

const CheckboxInput = styled.input`
  &:checked, &:not(:checked) {
  display: none;
 }`;

 const CheckboxLabel = styled.label`
  cursor: pointer;
  -webkit-user-select: none; /* Chrome/Safari */
  -moz-user-select: none; /* Firefox */
  -ms-user-select: none; /* IE10+ */
`;

class LoginForm extends React.Component {

    constructor() {
        super()
    }

    render() {
        return (

            <div>
                <form>
                    <SvgLogo/>
                    <InputField type="email" placeholder="email" id="email" name="email"/>
                    <InputField type="password" placeholder="password" id="password" name="password"/>
                    < CheckboxInput />
                    <Anchor href="#" value="test">Wachtwoord vergeten?</Anchor>
                    <Button value="Login"></Button>
                    <Anchor href="#" value="test">Registreer</Anchor>
                </form>
            </div>

        );
    }
}

export default LoginForm
