import React from 'react'
import styled from 'styled-components'


class CompostTransition extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      transition: ''
    }
  }

  componentDidMount() {
    this.getTransition()
  }

  getTransition() {
      this.setState({transition: 'Fase 1'})
  }

  render() {
    return(
      <div>
          <p> {this.state.transition} </p>
      </div>
    )
  }
}

export default CompostTransition;
