class TaskList extends React.Component {

    removeItem(item, i) {
        this.props.removeTask(item, i)
    }

    render() {
      return(
          <ul>
              { this.props.tasks.map((task, i) => {
                   return <li onClick={() => { this.removeItem(task, i)}} key={i}>{ task }</li>
               })}
          </ul>
      )
    }
}

export default TaskList;
