import React from 'react'
import styled from 'styled-components';


class CurrentDay extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      day: ""
    }

    this.getCurrentDay = this.getCurrentDay.bind(this)
  }

  componentDidMount() {
      this.getCurrentDay()
  }

  getCurrentDay() {

      let startDate = new Date('November 26, 2019 00:00:00'); // Dummy data
      let currentDate = new Date();

      const oneDay = 24 * 60 * 60 * 1000;

      const diffDays = Math.round(Math.abs((startDate.getTime() - currentDate.getTime()) / (oneDay)));

      let testDay = 0;
      startDate = testDay;

      const endResult = testDay + diffDays;


      this.setState({day: endResult})
  }

  render() {
    return(
      <div>
        <h2> Dag {this.state.day} </h2>
      </div>
    )
  }
}


export default CurrentDay;
