import React from 'react'
import styled from 'styled-components';

const startDate = new Date('November 20');

class CurrentDay extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      day: ""
    }

    this.getCurrentDay = this.getCurrentDay.bind(this)
  }

  componentDidMount() {
      this.getCurrentDay()
  }

  getCurrentDay() {
      const convertedStart = startDate.setDate(1);
      this.setState({day: convertedStart})
  }

  render() {
    return(
      <div>
        <h2> Dag {this.state.day} </h2>
      </div>
    )
  }
}


export default CurrentDay;
